import { useEffect, useState } from "react";
import axios from "axios";
import NewCard from "./components/NewsCard";
// import "./components/newpage.css";
const NewsPage = () => {
  const [articals, setArtical] = useState([]);
  const [isloading, setIsloading] = useState(true);
  const [totalpages, getTotalpages] = useState(0);

  useEffect(() => {
    setIsloading(true);
    const fetchData = async () => {
      try {
        const { data } = await axios.get(
          "http://hn.algolia.com/api/v1/search?"
        );
        const { hits, nbPages } = data;
        console.log(data, "ffff");
        setArtical(hits);
        getTotalpages(nbPages);
        console.log(data);
      } catch (err) {
        console.log(err);
      } finally {
        setIsloading(false);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="maincontainer">
      <h1> Hacked News</h1>
      <div className="news-container">
        {isloading ? (
          <p>loading....</p>
        ) : (
          articals.map((item) => <NewCard artical={item} key={item.onjectID} />)
        )}
      </div>
    </div>
  );
};

export default NewsPage;
